INSERT INTO `kitapeduli_album` VALUES (8,'Kegiatan Kita Peduli 2001','kegiatan-kita-peduli-2001','Awal terbentuknya kegiatan Kita Peduli','<table style=\"width: 546px;\" border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\n<tbody>\n<tr>\n<td valign=\"top\" width=\"36\">\n<p align=\"center\"><span style=\"font-family: calibri,arial,sans-serif; font-size: medium;\"><strong>NO</strong></span></p>\n</td>\n<td width=\"115\">\n<p align=\"center\"><span style=\"font-family: calibri,arial,sans-serif; font-size: medium;\"><strong>BULAN</strong></span></p>\n</td>\n<td width=\"480\">\n<p align=\"center\"><span style=\"font-family: calibri,arial,sans-serif; font-size: medium;\"><strong>KEGIATAN</strong></span></p>\n</td>\n</tr>\n<tr>\n<td valign=\"top\" width=\"36\">\n<p align=\"center\"><span style=\"font-family: calibri,arial,sans-serif; font-size: medium;\">1</span></p>\n</td>\n<td valign=\"top\" width=\"115\">\n<p><span style=\"font-family: calibri,arial,sans-serif; font-size: medium;\">&nbsp;awal tahun 2001</span></p>\n</td>\n<td valign=\"top\" width=\"480\">\n<ul>\n<li><span style=\"font-family: calibri,arial,sans-serif; font-size: medium;\">Bencana gempa bumi Bengkulu, Sumatera.</span></li>\n<li><span style=\"font-family: calibri,arial,sans-serif; font-size: medium;\">Bencana tanah longsor di Sukabumi, Jawa Barat.</span></li>\n<li><span style=\"font-family: calibri,arial,sans-serif; font-size: medium;\">Serta banjir diberbagai daerah di Indonesia.</span></li>\n</ul>\n</td>\n</tr>\n</tbody>\n</table>','','',1,'2013-03-26 09:56:41'),(11,'Kegiatan Kita Peduli 2002','kegiatan-kita-peduli-2002','Kegiatan bakti sosial selama tahun 2002','<table style=\"width: 546px;\" border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\n<tbody>\n<tr>\n<td valign=\"top\" width=\"36\">\n<p align=\"center\"><span style=\"font-family: calibri,arial,sans-serif; font-size: medium;\"><strong>NO</strong></span></p>\n</td>\n<td width=\"115\">\n<p align=\"center\"><span style=\"font-family: calibri,arial,sa